import React, { useState, useEffect } from 'react';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';
import axios from 'axios';

const Chat = () => {
    const [message, setMessage] = useState('');
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [loading, setLoading] = useState(true);
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token');

    const { 
        messages, 
        sendMessage, 
        connectionStatus 
    } = useWebSocket('ws://localhost:8081/websocket');

    useEffect(() => {
        const verifyAuth = async () => {
            try {
                const response = await axios.get('http://localhost:8081/validate-token', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setIsAuthenticated(response.data.valid);
            } catch (error) {
                setIsAuthenticated(false);
            } finally {
                setLoading(false);
            }
        };

        if (token && userId) {
            verifyAuth();
        } else {
            setLoading(false);
        }
    }, [token, userId]);

    if (loading) {
        return <div>Checking authentication...</div>;
    }

    if (!isAuthenticated) {
        return <div>Please login to use chat. User ID: {userId}</div>;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim()) {
            sendMessage({
                content: message,
                userId: userId,
                username: localStorage.getItem('username'),
                timestamp: new Date().toISOString()
            });
            setMessage('');
        }
    };

    return (
        <div className="chat-container">
            <div className="connection-status">
                Status: {connectionStatus}
            </div>
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className="message">
                        <span className="user">{msg.username || msg.userId}: </span>
                        <span className="content">{msg.content}</span>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button 
                    type="submit"
                    disabled={connectionStatus !== 'connected'}
                >
                    Send
                </button>
            </form>
        </div>
    );
};

export default Chat;
