import React, { useState, useEffect } from 'react';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('userId');
    const username = localStorage.getItem('username');
    const token = localStorage.getItem('token');

    const { 
        messages, 
        sendMessage, 
        connectionStatus 
    } = useWebSocket(token ? 'ws://localhost:8081/websocket' : null);

    if (!token || !userId) {
        return <div className="chat-auth-message">Please login to use chat</div>;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim() && connectionStatus === 'connected') {
            sendMessage({
                type: "message",
                content: message,
                userId,
                username,
                timestamp: new Date().toISOString()
            });
            setMessage('');
        }
    };


    return (
        <div className="chat-container">
            <div className={`connection-status ${connectionStatus}`}>
                Status: {connectionStatus}
                {connectionStatus === 'error' && (
                    <button onClick={() => window.location.reload()}>
                        Reconnect
                    </button>
                )}
            </div>

            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.type || 'user'}`}>
                        {msg.type === 'system' ? (
                            <div className="system-message">{msg.content}</div>
                        ) : (
                            <>
                                <span className="user">{msg.username || msg.userId}: </span>
                                <span className="content">{msg.content}</span>
                                <span className="time">
                                    {new Date(msg.timestamp).toLocaleTimeString()}
                                </span>
                            </>
                        )}
                    </div>
                ))}
            </div>

            <form onSubmit={handleSubmit} className="message-form">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button 
                    type="submit"
                    disabled={connectionStatus !== 'connected' || !message.trim()}
                >
                    Send
                </button>
            </form>
        </div>
    );
};

export default Chat;
