import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import '../MainLayout.css';

const PostList = ({ refreshTrigger }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Wrap fetchPosts in useCallback to prevent unnecessary re-creations
    const fetchPosts = useCallback(async () => {
        let isMounted = true;

        try {
            setLoading(true);
            setError(null);

            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Authentication token not found');
            }

            const response = await axios.get('http://localhost:8081/posts', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Accept': 'application/json'
                }
            });

            if (!isMounted) return;

            // Debug: log the raw response
            console.log('Raw response:', response.data);

            // Handle both possible response structures
            const postsData = response.data.data || response.data;

            if (!postsData || !Array.isArray(postsData)) {
                throw new Error('Invalid data structure received from server');
            }

            const processedPosts = postsData
                .filter(post => {
                    if (!post?.id) {
                        console.error('Post missing ID:', post);
                        return false;
                    }
                    return true;
                })
                .map(post => ({
                    id: post.id,
                    title: post.title || 'Untitled Post',
                    content: post.content || 'No content available',
                    author_id: post.author_id || 'Unknown',
                    created_at: post.created_at || new Date().toISOString()
                }));

            setPosts(processedPosts);
        } catch (err) {
            if (!isMounted) return;

            console.error('Post fetch error:', {
                error: err,
                response: err.response
            });

            setError(
                err.response?.data?.error || 
                err.response?.data?.message || 
                err.message || 
                'Failed to load posts. Please try again later.'
            );
        } finally {
            if (isMounted) {
                setLoading(false);
            }
        }
    }, []);

    useEffect(() => {
        const abortController = new AbortController();

        fetchPosts();

        return () => {
            abortController.abort(); // Cancel pending request on unmount
        };
    }, [fetchPosts, refreshTrigger]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading posts...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <p className="error-message">{error}</p>
                <button
                    onClick={fetchPosts}
                    className="retry-button"
                >
                    Retry
                </button>
            </div>
        );
    }

    return (
        <div className="post-list-container">
            {posts.length > 0 ? (
                [...posts].reverse().map(post => (
                    <div key={`post-${post.id}`} className="post-item">
                        <h3>{post.title}</h3>
                        <p className="post-content">{post.content}</p>
                        <div className="post-meta">
                            <span className="author">Автор: {post.author_name}</span>
                            <span className="date"> | Дата: {new Date(post.created_at).toLocaleString()}</span>
                        </div>
                    </div>
                ))
            ) : (
                <div className="no-posts-message">
                    <p>Пока нет постов. Будьте первым!</p>
                </div>
            )}
        </div>
    );
};

export default PostList;
